package codex;

public class Problem8 {
public static void main(String args[])
{
	String num = "";
	long maximum=0;
	for(int p=0;p< num.length()-13;p++)
	{
		long prd=1;
		for(int s=p;s<p+13;s++)
		{
			prd=prd*Character.getNumericValue(num.charAt(s));
		}
		if(prd >maximum)
		{
			maximum=prd;
		}
			
	}
	System.out.println(maximum);
	   
}


}
